mapper.select:
SELECT b.id, b.create_time, b.username, b.real_name, b.avatar, b.`status` FROM `sys_user_role` a LEFT JOIN sys_user b ON a.user_id = b.id WHERE b.del_flag = 0
AND a.role_id = #{param.roleId}
AND b.username = #{param.username}
AND b.real_name like concat('%', #{param.realName}, '%')

SELECT b.id, b.create_time, b.username, b.real_name, b.avatar, b.`status` FROM sys_user b WHERE b.id NOT IN ( SELECT a.user_id FROM sys_user_role a
a.role_id = #{param.roleId}
) AND b.del_flag = 0
AND b.username = #{param.username}
AND b.real_name like concat('%', #{param.realName}, '%')
