mapper.select:
SELECT p.id, p.project_name, p.project_description, p.logo, up.user_type, p.create_time FROM sys_projects p JOIN sys_projects_users up ON p.id = up.project_id
p.del_flag = '0' AND up.user_id = #{query.userId}
AND p.project_name LIKE #{projectNameLike}
ORDER BY p.create_time DESC

SELECT p.id, p.project_name, p.project_description, p.logo, up.user_type, p.create_time FROM sys_projects p JOIN sys_projects_users up ON p.id = up.project_id
p.del_flag = '0' AND up.project_id = #{projectId} AND up.user_id = #{userId}
ORDER BY p.create_time DESC
